import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  constructor(private http: HttpClient) { }

  login() {
    const credentials = {
      username: this.username,
      password: this.password,
    };

    this.http.post('http://localhost:5157/api/login', credentials)
      .subscribe(
        (response) => {
          console.log("Login successful"); // You can perform redirection or other actions here
        },
        (error) => {
          console.log("Login failed"); // Handle login error (e.g., display an error message)
        }
      );
  }
}
<div class="centered-text">
  <h1 class="text-white">Login</h1>
</div>
<br>
<div class="centered-text">
  <label for="username" class="text-white">Username:</label>
  <input type="text" id="username" name="username" [(ngModel)]="username" required placeholder="Username" />
</div>

<div class="centered-text">
  <label for="password" class="text-white">Password:</label>
  <input type="password" id="password" name="password" [(ngModel)]="password" required placeholder="Password" />
</div>

<div class="centered-text">
  <p class="text-white">Don't have an account? <a href="#">Sign Up</a></p>
  <button (click)="login()" class="btn btn-primary">Login</button>
</div>
