import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {
  username: string = '';
  password: string = '';
  errorMessage: string = ''; 

  constructor(private http: HttpClient, private router: Router) { }

  login() {
    const credentials = {
      username: this.username,
      password: this.password,
    };
    console.log(credentials);
    this.http.post('http://localhost:5157/api/login', credentials)
      .subscribe(
        (response: any) => {
          console.log("Login successful");
          this.router.navigate(['/enchere']);
        },
        (error) => {
          console.log("Login failed");
          this.errorMessage = 'Login failed. Please check your credentials.';
        }
      );

  }
}
<style>
  .centered-text {
    text-align: center;
  }
</style>
<div class="centered-text">
  <h1 class="text-white">Login</h1>
</div>
<br>
<div class="centered-text">
  <label style="color: white; font-weight: normal; font-size: 20px; margin-bottom: 20px; display: inline-block; width: 100px;" for="username">Username:</label>
  <input type="text" style="border: 1px solid #A3A3A3; background-color: #A3A3A3; height: 45px; border-radius: 20px; font-size: 18px; padding: 10px; width: 350px; display: inline-block;" id="username" name="username" [(ngModel)]="username" required placeholder="Username" />
</div>

<div class="centered-text">
  <label style="color: white; font-weight: normal; font-size: 20px; margin-bottom: 20px; display: inline-block; width: 100px;" for="password">Password:</label>
  <input type="password" style="border: 1px solid #ccc; background-color: #A3A3A3; height: 45px; border-radius: 20px; font-size: 18px; padding: 10px; width: 350px; display: inline-block;" id="password" name="password" [(ngModel)]="password" required placeholder="Password" />
</div>
<div class="centered-text">
  <p class="text-white">Don't have an account? <a href="/signup">Sign Up</a></p>
  <button (click)="login()" class="btn " style="background-color: #A92222; color: white;">Login</button>
</div>
